fn main () {
    let input = get_input();

    let mut level = 0;

    let mut pos = 1;
    let mut not_been_in_basement = true;

    for c in input.chars() {
        if c == '(' {
            level += 1;
        } else {
            level -= 1;
        }

        if not_been_in_basement && level < 0 {
            println!("{} char is first to enter basement", pos);
            not_been_in_basement = false;
            // 1782 too low
        }

        pos += 1;
    }

    println!("{}", level)
}

