package knowit2017;

/**
 * Day22
 */
public class Day22 {
  static final String T = "ABCDA";
  // static final String T = "ABC";
  static final String S ="";
  // static final String S = "ADOBECODECBFANETS";

  public static void main(String[] args) {
    int i = 0;
    int j = S.length();
    String minSub = S;

    while (i < S.length()) {
      String s = S.substring(i, j);
      int k = s.length();
      if (hasAllCharsInT(s)) {
        if (s.length() <= minSub.length()) {
          minSub = s;
        }
        j--;
      } else {
        i++;
        j = S.length();
      }
    }

    System.out.print(minSub);
    // ikke AYBUHCERJXD
    // ikke DCYAVICPTNB
  }

  static boolean hasAllCharsInT(String s) {
    return T.chars()
      .mapToObj(ch -> s.indexOf(ch) != -1)
      .reduce(true, (ret, b) -> ret && b)
    && s.indexOf('A') != s.lastIndexOf('A');
  }
}